import { myAxios } from "./Helper";

// âœ… LOGIN
export const loginUser = (userData) => {
  return myAxios.post("/auth/login", userData)
    .then((res) => res.data); // backend already returns user + token
};

// âœ… Step 1: Get OTP from backend
export const getOtpFromBackend = (userData) => {
  // ðŸ‘‡ send correct field name as per backend DTO
  const payload = {
    userName: userData.username,
    email: userData.email,
    password: userData.password,
  };

  return myAxios.post("/auth/register", payload)
    .then((res) => res.data);
};

// âœ… Step 2: Verify & register user
export const registerUser = (userData) => {
  const payload = {
    userName: userData.username,
    email: userData.email,
    password: userData.password,
  };

  return myAxios.post("/auth/verify", payload)
    .then((res) => res.data);
};

// âœ… CREATE LOBBY
export const createLobby = (data) => {
  return myAxios.post("/lobby/create", data)
    .then((res) => res.data);
};

// âœ… CREATE ROOM
export const createRoom = (data) => {
  return myAxios.post("/room/create", data)
    .then((res) => res.data);
};
