import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { getOtpFromBackend, registerUser } from "../services/user-service";
import "../styles.css";

export default function SignUpPage() {
  const navigate = useNavigate();

  const [step, setStep] = useState(1);
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [backendOtp, setBackendOtp] = useState("");
  const [enteredOtp, setEnteredOtp] = useState("");

  const handleSendOtp = async (e) => {
    e.preventDefault();

    try {
      // ‚úÖ Backend /auth/verify returns OTP in res.token
      const res = await getOtpFromBackend({ username, email, password });

      const otpFromBackend = String(res.token);
      setBackendOtp(otpFromBackend);

      console.log("OTP from backend:", otpFromBackend); // remove later
      alert("‚úÖ OTP sent to your email!");
      setStep(2);
    } catch (err) {
      console.error(err);
      alert("‚ùå Unable to send OTP");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();

    if (enteredOtp !== backendOtp) {
      alert("‚ùå Wrong OTP");
      return;
    }

    try {
      // ‚úÖ Now call register API to create user
      const res = await registerUser({ username, email, password });

      // ‚úÖ Save user data after register
      localStorage.setItem("token", res.token);
      localStorage.setItem("username", res.username);
      localStorage.setItem("email", res.email);

      alert("‚úÖ Account created successfully!");
      navigate("/"); // redirect anywhere
    } catch (err) {
      console.error(err);
      alert("‚ùå Registration failed");
    }
  };

  return (
    <div className="login-container">
      <h1>{step === 1 ? "Create Account üöÄ" : "Verify OTP üîê"}</h1>

      {step === 1 && (
        <form onSubmit={handleSendOtp} className="login-form">
          <input 
            type="text" 
            placeholder="Username" 
            value={username}
            onChange={(e)=>setUsername(e.target.value)}
            required 
          />
          <input 
            type="email" 
            placeholder="Email"
            value={email}
            onChange={(e)=>setEmail(e.target.value)}
            required
          />
          <input 
            type="password" 
            placeholder="Password"
            value={password}
            onChange={(e)=>setPassword(e.target.value)}
            required
          />

          <button type="submit">Send OTP</button>

          <p>
            Already have an account? <Link to="/login">Login</Link>
          </p>
        </form>
      )}

      {step === 2 && (
        <form onSubmit={handleRegister} className="login-form">
          <input 
            type="text" 
            placeholder="Enter OTP"
            value={enteredOtp}
            onChange={(e)=>setEnteredOtp(e.target.value)}
            required
          />

          <button type="submit">Verify & Register</button>
        </form>
      )}
    </div>
  );
}
