import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { registerUser, verifyOtp } from "../services/user-service";
import "../styles.css";

export default function SignUpPage() {
  const navigate = useNavigate();

  const [step, setStep] = useState(1);
  const [username, setUsername] = useState("");
  const [email, setEmail]     = useState("");
  const [password, setPassword] = useState("");
  const [otp, setOtp] = useState("");

  const [tempToken, setTempToken] = useState("");

  // Step 1: Send Signup Request + OTP
  const handleSignup = async (e) => {
    e.preventDefault();
    
    try {
      const res = await registerUser({ username, email, password });

      // ‚úÖ backend gave us temp token (for OTP verify)
      setTempToken(res.token);

      setStep(2);
      console.log(res.token)
      alert("‚úÖ OTP sent to your email");
    } catch (err) {
      console.log(err);
      alert("‚ùå Signup failed");
    }
  };

  // Step 2: Verify OTP
  const handleVerifyOtp = async (e) => {
    e.preventDefault();

    try {
      await verifyOtp(otp, tempToken);
      alert("‚úÖ Account verified & logged in");
      navigate("/");
    } catch (err) {
      console.log(err);
      alert("‚ùå OTP verification failed");
    }
  };

  return (
    <div className="login-container">
      <h1 className="login-title">{ step === 1 ? "Create Account üöÄ" : "Verify OTP üîê" }</h1>

      {/* Signup Screen */}
      {step === 1 && (
        <form className="login-form" onSubmit={handleSignup}>
          <div className="input-group">
            <label>Username</label>
            <input 
              type="text" 
              value={username}
              onChange={(e)=>setUsername(e.target.value)}
              placeholder="Enter username" 
              required
            />
          </div>

          <div className="input-group">
            <label>Email</label>
            <input 
              type="email" 
              value={email}
              onChange={(e)=>setEmail(e.target.value)}
              placeholder="Enter email" 
              required
            />
          </div>

          <div className="input-group">
            <label>Password</label>
            <input 
              type="password" 
              value={password}
              onChange={(e)=>setPassword(e.target.value)}
              placeholder="Enter password" 
              required
            />
          </div>

          <button className="login-btn" type="submit">Send OTP</button>

          <p className="signup-text">
            Already have an account? <Link className="signup-link" to="/login">Login</Link>
          </p>
        </form>
      )}

      {/* OTP Screen */}
      {step === 2 && (
        <form className="login-form" onSubmit={handleVerifyOtp}>
          <div className="input-group">
            <label>Enter OTP</label>
            <input 
              type="text" 
              value={otp}
              onChange={(e)=>setOtp(e.target.value)}
              placeholder="Enter OTP" 
              required
            />
          </div>

          <button className="login-btn" type="submit">Verify OTP</button>
        </form>
      )}
    </div>
  );
}
