import { myAxios } from "./Helper";

// âœ… LOGIN
export const loginUser = (userData) => {
  return myAxios.post("/auth/login", userData)
    .then((res) => {
      const { token, username, email } = res.data;
      localStorage.setItem("token", token);
      localStorage.setItem("username", username);
      localStorage.setItem("email", email);
      return res.data;
    });
};

// âœ… Step 1: Get OTP from backend
export const getOtpFromBackend = (userData) => {
  return myAxios.post("/auth/verify", userData)  // ðŸ‘ˆ verify returns OTP only!
    .then((res) => res.data); 
};

// âœ… Step 2: Register user after OTP success
export const registerUser = (userData) => {
  return myAxios.post("/auth/register", userData) // ðŸ‘ˆ register final signup
    .then((res) => {
      const data = res.data;
      localStorage.setItem("token", data.token);
      localStorage.setItem("username", data.username);
      localStorage.setItem("email", data.email);
      return data;
    });
};

// âœ… CREATE LOBBY
export const createLobby = (data) => {
  return myAxios.post("/lobby/create", data)
    .then((res) => {
      const { role } = data;
      const { roomId } = res.data;

      localStorage.setItem("role", role);
      localStorage.setItem("roomId", roomId);

      return res.data;
    });
};

// âœ… CREATE ROOM
export const createRoom = (data) => {
  return myAxios.post("/room/create", data)
    .then((res) => {
      const { role } = data;
      const { roomId } = res.data;

      localStorage.setItem("role", role);
      localStorage.setItem("roomId", roomId);

      return res.data;
    });
};
